    SOP_T.R$Inst <- gsub(" Mgt", "", SOP_T.R$Inst)
    SOP_T.R$Inst <- gsub("^ *", "", SOP_T.R$Inst)
    
    ## Reorder columns in HA dataframe
    
    SOP_T.R.HA <- SOP_T.R.HA[,c(14, 2:13, 1)]
    
    ## Combine dataframes
    
