    for(i in 1:(length(Tri_i2)-1)){
        AE_T$Triage[(Tri_i2[i]+1):(Tri_i2[i+1]-1)] <- AE_T$Triage[Tri_i2[i]]
    }
    
    # Drop empty rows
    
    AE_Tr <- AE_T[-c(1, which(is.na(AE_T[,2]))),]
    
    # Tidy Inst names
    
    AE_Tr$Inst <- gsub("Overall$", "HA", AE_Tr$Inst)
