    HIP_Tr <- dcast(HIP_Tr, Order + Inst ~ Month, value.var = "PercentWithin")
    
    # Move Inst to row names
    
    row.names(HIP_Tr) <- HIP_Tr$Inst
    
    # Remove excess columns
    
    HIP_Tr <- HIP_Tr %>% select(-Order, -Inst)
    
    
    # Return dataframe
    
    HIP_Tr
    
